import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex)
const baseUrl = "https://dummyjson.com";
const endpointUserList = "/Users"

 function fetchData(baseUrl, endPoint, method) {
   const data = fetch(`${baseUrl}${endPoint}`, {
        method: method,
    })
        .then((res) => res.json())
        .then(data => {
            return data;
        });
        return data
}
export const store = new Vuex.Store({
    state: {
        usersList: [],
    },
    getters: {
        getAllUsers() {
            return store.state.usersList
        }

    },
    mutations: {
        userListUpdate(context, userList) {
            console.log(userList)
            this.state.usersList = userList
        },
        deleteUser(state, id) {
            state.usersList = state.usersList.filter((user) => {
                user.id != id;
            })
        },
        addUser(state, userData) {
            state.usersList.shift(userData)
        },
    },
    actions: {

        userListUpdate(context) {
            const data = fetchData(baseUrl, endpointUserList, 'GET')
            context.commit('userListUpdate', data)
        }
    }
})
