import Vue from "vue";
import Vuex from "vuex";

Vue.use(Vuex)
const baseUrl = "https://dummyjson.com";
const endpointUserList = "/Users"
const endpointAdd = "/users/add"

function fetchData(baseUrl, endPoint, method, userData) {
    const data = fetch(`${baseUrl}${endPoint}`, {
        method: method,
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(userData)
    })
        .then((res) => res.json())
        .then(data => {
            return data;
        });
    return data
}
export const store = new Vuex.Store({
    state: {
        usersList: [],
    },
    getters: {
        getAllUsers() {
            return store.state.usersList
        }

    },
    mutations: {
        userListUpdate(context, userList) {
            this.state.usersList = userList
        },
        deleteUser(state, userData) {
            this.state.usersList = this.state.usersList.filter((user) => {
                user.id != userData.id;
            })
        },
        addUser(state, userData) {
            this.state.usersList.shift(userData)
        },
        updateUser(state, userData){
            const index =  this.state.userList.findIndex(user => {
                return user.id == userData.id
            })
            this.state.userList.splice(index,1)
        }
    },
    actions: {
        async userListUpdate(context, userData) {
            const data = await fetchData(baseUrl, endpointUserList, 'GET', userData)
            context.commit('userListUpdate', data)
        },
        async deleteUser(context, userData) {
            const endpointDelete = `users${userData.id}`
            const data = await fetchData(baseUrl, endpointDelete, 'DELETE', userData)
            context.commit('deleteUser', userData)
        },
        async addUser(context, userData) {
            const data = await fetchData(baseUrl, endpointAdd, 'POST', userData)
            context.commit('addUser', userData)
        },
        async updateUser(context, userData) {
            const endpointUpdate = `users${userData.id}`
            const data = await fetchData(baseUrl, endpointUpdate, 'PUT', userData)
            context.commit('updateUser', userData)
        },
    }
})
